div.script:
async function getInfo() {
  let town = document.getElementById('townInput').value;
  let apiKey = "101d9b88089c209d67d9ae493ba1f4c7";
  let response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${town}&appid=${apiKey}`);

  let jsonifiedOutput = await response.json();
  var cTown = town.charAt(0).toUpperCase() + town.slice(1)
  document.getElementById('location').innerHTML = "Location: " + cTown;

  if (jsonifiedOutput.base) {
    document.getElementById('Status').innerHTML = "Debugging: Okay";
  } else {
    var errormsg = "Error: Could not find the city " + cTown 
    var error = errormsg.fontcolor("red");
    document.getElementById('Status').innerHTML = error;
     document.getElementById('type').innerHTML = "Type of Weather: Error";
     document.getElementById('temperature').innerHTML = "Temperature: Error";
    document.getElementById('feelsLike').innerHTML = "Feels Like: Error";
  }


  document.getElementById('type').innerHTML = "Type of Weather: " + jsonifiedOutput.weather[0].main;

  let temperature = 9/5 * (jsonifiedOutput.main.temp - 273) + 32
  document.getElementById('temperature').innerHTML = "Temperature: " + temperature.toFixed(2);

  let feelsLikeTemp = 9/5 * (jsonifiedOutput.main.feels_like - 273) + 32;
  document.getElementById('feelsLike').innerHTML = "Feels Like: " + feelsLikeTemp.toFixed(2);
}
// API Key: 101d9b88089c209d67d9ae493ba1f4c7
